mapper.select:
select * from ac_items

    status=1
    and concat(name,author) like #{keyword}

select * from ac_items

    status=1


            AND ${field} like #{word}



            AND CONCAT(`name`,`author`,`info`,`country`,`year`) LIKE #{word}











        ORDER BY CONVERT(${orderField} USING gbk) COLLATE gbk_chinese_ci ${orderType}


        ORDER BY ${orderField} ${orderType}

select * from ac_items

    status=1
    and ${field} LIKE #{word}




        ORDER BY CONVERT(${orderField} USING gbk) COLLATE gbk_chinese_ci ${orderType}


        ORDER BY ${orderField} ${orderType}



    limit #{start},#{size}

SELECT * FROM ac_items LEFT JOIN (select ac_id,count(*) count FROM mark GROUP BY ac_id) c ON id=ac_id WHERE `status`=1
AND category = #{category}
ORDER BY `count` DESC,rating DESC
limit #{start},#{size}

SELECT *
FROM ac_items
WHERE status = 1
  AND `name` = #{name}
  AND `year` = #{year}
  AND `author` = #{author}
  AND `category` = #{category}

mapper.update:
update ac_items
set status=#{status}

    ,image=''

where id = #{id}

update ac_items
SET rating=#{rating}
WHERE id = #{acId}
