mapper.select:
select * from ac_items

    status=1
    and ${field} like #{word}

select * from ac_items

    status=1
    and concat(name,author) like #{keyword}

select * from ac_items

    status=1
    and ${field} LIKE #{word}



    ORDER BY ${field} ${seq}


    limit #{start},#{size}

SELECT * FROM ac_items LEFT JOIN (select ac_id,count(*) count FROM mark GROUP BY ac_id) c ON id=ac_id WHERE `status`=1
AND category = #{category}
ORDER BY `count` DESC,rating DESC
limit #{start},#{size}

SELECT * FROM ac_items
WHERE status=1
AND `name`=#{name}
AND `year`=#{year}
AND `author`=#{author}
AND `category`=#{category}

mapper.update:
update ac_items
SET name=#{name},author=#{author},year=#{year},info=#{info},image=#{image},
    category=#{category},country=#{country}
where id = #{id}

update ac_items
set status=#{status}

    ,image=''

where id = #{id}

update ac_items
SET rating=#{rating}
WHERE id = #{acId}
